package Lec_5;
import java.util.*;

public class Pattern_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		
		
		int nst=1;
		
		while(nst<=n) {
			
			System.out.print("*");
			nst=nst+1;
		}
		
	}

}
